      for (var i = 0, f; f = files[i]; i++) {
        if (!f.type.match('image.*')) {
          continue;
        }

        var reader = new FileReader();

        reader.onload = function (theFile) {
          return function (e) {
            var span = document.createElement('span');
            span.innerHTML = ['<div class="prev_img" style="display: block;"><img style="width:100%; height:100%;" src="', e.target.result, '" title="', escape(theFile.name), '"/><br><div class="img_del"><i class="fas fa-times-circle"></i></div></div>'].join('');
            document.querySelector('.prev_upload').insertBefore(span, null);
            const muti_img_list = document.querySelectorAll(".img_del");

            for (let i = 0; i < muti_img_list.length; i++) {
              muti_img_list[i].addEventListener('click', function () {
                this.parentNode.remove();
                console.log(i);
              });
            }
          };
        }(f);

        reader.readAsDataURL(f);
      }
    } else {
      alert("사진은 6개까지 첨부 가능합니다");
    }
  }

  document.getElementById('files').addEventListener('change', handleFileSelect, false);
};

